#!/bin/bash
# Script to set up SSH keys on the Database VM (10.0.10.169)

# Create .ssh directory if it doesn't exist
mkdir -p ~/.ssh
chmod 700 ~/.ssh

# Add public keys of all other VMs to authorized_keys
cat >> ~/.ssh/authorized_keys << 'EOT'
# Frontend VM (10.0.8.49) public key
ssh-rsa  gc359@njit.edu

# Backend VM (10.0.0.22) public key
ssh-rsa  ubuntu@ip-10-0-13-3

# Messaging VM (10.0.0.21) public key
ssh-rsa  ubuntu@ip-10-0-0-21
EOT

# Set proper permissions on authorized_keys
chmod 600 ~/.ssh/authorized_keys

echo "SSH keys for other VMs have been added to authorized_keys on Database VM"
